INSERT INTO `rex_module` VALUES 
  (1,'01 . Titel / Intro','<?php\r\n// Ist Überschrift vorhanden?\r\nif (\"REX_VALUE[1]\" != \'\') {\r\n\r\n	// Wenn ein einspaltiges Template gewählt ist, wird die Bootstrap-CSS-Klasse .container gesetzt, um einen Einzug zu ermöglichen\r\n	?>\r\n	<div class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>mtb\">\r\n		<div class=\"row\">\r\n			<div class=\"<?php if ($this->getTemplateId() == 1) echo \'col-lg-8 col-lg-offset-2 \'; ?>centered\">\r\n				<h2>REX_VALUE[1]</h2>\r\n				<br>\r\n				<div class=\"hline\"></div>\r\n			</div>\r\n		</div>\r\n	</div><! --/container -->\r\n<?php } ?>\r\n','<!-- *******************************************************\r\nTitel / Intro\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Titel / Intro</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <div class=\"col-sm-12\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-11-30 17:31:33','2016-03-19 12:37:06','',0),
  (2,'02a . Text (Textile und Rex_Markitup)','<?php\r\n$template = ($this->getTemplateId() == 1) ? \' container\' : \'\';\r\n$distance = (\"REX_VALUE[19]\" != \'\') ? \' REX_VALUE[19]\' : \'\';\r\n$background = (\"REX_VALUE[16]\" == \'true\') ? \' grey\' : \'\';\r\n\r\necho \'\r\n<section class=\"module-wrapper clearfix\'.$background.\'\">\r\n<div class=\"full\'.$template.$distance.\'\">\';\r\n\r\n	// Linker und recter Einzug?\r\n	if (\"REX_VALUE[17]\" == \'vd\') {\r\n		echo \'\r\n		<div class=\"row\">\r\n		<div class=\"col-md-8 col-md-offset-2\">\';\r\n	}\r\n\r\n	if (\"REX_VALUE[1]\" != \'\') {\r\n		echo \'\r\n		<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>\';\r\n	}\r\n\r\n	if (\'REX_VALUE[id=3 isset=1]\') {\r\n		// Der Text wird durch Textile geparst\r\n	    $textile = \'REX_VALUE[id=3 output=html]\';\r\n	    $textile = rex_textile::parse($textile);\r\n		// Bold-Tags werden \"missbraucht\", um Bootstrap-Labels setzen zu können.\r\n	    echo str_replace(array(\'<b>\', \'</b>\'), array(\'<span class=\"label label-primary\">\', \'</span>\'), $textile);\r\n	}\r\n\r\n	// Trennlinie?\r\n	if (\"REX_VALUE[18]\"== \'true\'){\r\n		echo \'\r\n		<div class=\"hline\"></div>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[17]\" == \'vd\') {\r\n		echo \'\r\n		</div>\r\n		</div>\';\r\n	}\r\n	?>\r\n</div>\r\n</section>\r\n','<!-- *******************************************************\r\nTEXT\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h2\'=>\'2. Ebene\',\r\n				\'h3\'=>\'3. Ebene\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[2]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[2]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_textile_1\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-textile_full\" id=\"markitup_textile_1\" name=\"REX_INPUT_VALUE[3]\">REX_VALUE[3]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"padding\">Einrückung</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keine\',\r\n				\'vd\'=>\'ja\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[17]\" id=\"padding\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[17]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n\r\n			<br><section class=\"rex-page-section\">\r\n			    <div class=\"panel panel-default\">\r\n\r\n			        <header class=\"panel-heading collapsed\" data-toggle=\"collapse\" data-target=\"#collapse-REX_SLICE_ID\" aria-expanded=\"false\">\r\n						<div class=\"panel-title\"><i class=\"rex-icon rex-icon-info\"></i> Hinweis</div>\r\n					</header>\r\n\r\n			        <div id=\"collapse-REX_SLICE_ID\" class=\"panel-collapse collapse\" aria-expanded=\"false\" style=\"height: 0px;\">\r\n\r\n			            <div class=\"panel-body\" style=\"background: #f3f6fb;\">\r\n			                <ul>\r\n								<li>Hier kann man eine linke und rechte Einrückung von je zwei Spalten des 12er-Bootstrap-Rasters aktivieren.</li>\r\n								<li>Der vertikale Abstand erzeugt einen oberen und unteren Zwischenraum von jeweils 60px.</li>\r\n							</ul>\r\n						</div>\r\n			        </div>\r\n			    </div>\r\n			</section>\r\n\r\n		</div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"distance\">vertikaler Abstand</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'mt\'=>\'oberhalb\',\r\n				\'mb\'=>\'unterhalb\',\r\n				\'mtb\'=>\'ober- und unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[19]\" id=\"distance\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Trennlinie</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'nein\',\r\n				\'true\'=>\'ja\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[18]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[18]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Hintergrund</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'weiß\',\r\n				\'true\'=>\'grau\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[16]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[16]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-11-30 17:32:04','2016-03-19 12:36:43','',0),
  (3,'03 . Text mit Bild (1 - 3 Spalten)','<?php\r\n$grid_content1 = \'\';\r\n$grid_content2 = \'\';\r\n$grid_content3 = \'\';\r\n\r\n// col1\r\nif (\"REX_MEDIA[1]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[1]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content1 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[1]\">\';\r\n}\r\nif (\'REX_VALUE[id=1 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=1 output=html]\';\r\n    $grid_content1 .= rex_textile::parse($textile);\r\n}\r\n\r\n// col2\r\nif (\"REX_MEDIA[2]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[2]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content2 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[2]\">\';\r\n}\r\nif (\'REX_VALUE[id=2 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=2 output=html]\';\r\n    $grid_content2 .= rex_textile::parse($textile);\r\n}\r\n\r\n// col3\r\nif (\"REX_MEDIA[3]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[3]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content3 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[3]\">\';\r\n}\r\nif (\'REX_VALUE[id=3 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=3 output=html]\';\r\n    $grid_content3 .= rex_textile::parse($textile);\r\n}\r\n?>\r\n<!-- SECTION -->\r\n<div class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>pictext<?php if (\"REX_VALUE[19]\" != \'\') echo \" REX_VALUE[19]\"; ?>\">\r\n\r\n	<?php\r\n	if (\"REX_VALUE[10]\" != \'\') {\r\n		echo \'\r\n		<REX_VALUE[11]>REX_VALUE[10]</REX_VALUE[11]>\';\r\n	}\r\n	?>\r\n		<div class=\"row\">\r\n\r\n		<?php\r\n		// 2cols\r\n		if (\"REX_VALUE[9]\" == \'2\')  {\r\n			echo \'\r\n			<div class=\"col-md-6\">\r\n				\'.$grid_content1.\'\r\n			</div>\r\n			<div class=\"col-md-6\">\r\n				\'.$grid_content2.\'\r\n			</div>\';\r\n		// 3cols\r\n		} elseif (\"REX_VALUE[9]\" == \'3\')	 {\r\n			echo \'\r\n			<div class=\"col-sm-4\">\r\n				\'.$grid_content1.\'\r\n			</div>\r\n			<div class=\"col-sm-4\">\r\n				\'.$grid_content2.\'\r\n			</div>\r\n			<div class=\"col-sm-4\">\r\n				\'.$grid_content3.\'\r\n			</div>\';\r\n		// 1col\r\n		} else {\r\n			echo $grid_content1;\r\n		}\r\n		?>\r\n\r\n	</div>\r\n\r\n	<?php\r\n	if (\"REX_VALUE[15]\"== \'true\'){\r\n	echo \'\r\n	<div class=\"hline\"></div>\';\r\n	}\r\n	?>\r\n</div>\r\n','<!-- *******************************************************\r\nTEXT UND BILD IN SPALTEN\r\n******************************************************** -->\r\n\r\n<script type=\"text/javascript\">\r\njQuery(function($){\r\n	$(document).ready(function(){\r\n\r\n		$(\'select#choices\').change(function() {\r\n			$(\'.off\').hide();\r\n\r\n			for(var i = 1; i <= parseInt($(this).val()); i++) {\r\n				$(\'#col\' + i).show();\r\n			}\r\n		});\r\n\r\n		$(\'select#choices\').change();\r\n\r\n	})\r\n});\r\n</script>\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"choices\">Spalten</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'1\'=>\'volle Breite\',\r\n				\'2\'=>\'zwei Spalten\',\r\n				\'3\'=>\'drei Spalten\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[9]\" id=\"choices\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[9]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div cla