  (4,'04 . Drei Teaserkästen','<!-- *******************************************************\r\nDREI TEASERKÄSTEN\r\n******************************************************** -->\r\n\r\n<?php\r\n$template = ($this->getTemplateId() == 1) ? \' container\' : \'\';\r\n$distance = (\"REX_VALUE[19]\" != \'\') ? \' REX_VALUE[19]\' : \'\';\r\n?>\r\n\r\n<section class=\"service clearfix\">\r\n	<div class=\"full<?php echo $template.$distance; ?>\">\r\n\r\n		<div class=\"row centered\">\r\n\r\n			<div class=\"col-md-4\">\r\n				<?php\r\n				if (\"REX_VALUE[2]\" != \'\') {\r\n					if (\"REX_VALUE[1]\" != \'\') {\r\n						echo \'\r\n						<i class=\"fa fa-REX_VALUE[1]\"></i>\';\r\n					}\r\n					echo \'\r\n					<h4>REX_VALUE[2]</h4>\';\r\n				}\r\n\r\n				if (\'REX_VALUE[id=3 isset=1]\') {\r\n				    $textile = \'REX_VALUE[id=3 output=html]\';\r\n				    echo rex_textile::parse($textile);\r\n				}\r\n\r\n				if (\"REX_LINK[1]\" != \'\') {\r\n					$link = rex_article::get(\"REX_LINK[1]\");\r\n					$link_name = $link->getValue(\'name\');\r\n					if (\"REX_VALUE[4]\" != \'\') {\r\n						$link_name = \'REX_VALUE[4]\';\r\n					}\r\n					echo \'\r\n					<p><br/><a href=\"\'.rex_getUrl(\"REX_LINK[1]\").\'\" class=\"btn btn-theme\">\'.$link_name.\'</a></p>\';\r\n				}\r\n				?>\r\n			</div>\r\n\r\n			<div class=\"col-md-4\">\r\n				<?php\r\n				if (\"REX_VALUE[6]\" != \'\') {\r\n					if (\"REX_VALUE[5]\" != \'\') {\r\n						echo \'\r\n						<i class=\"fa fa-REX_VALUE[5]\"></i>\';\r\n					}\r\n					echo \'\r\n					<h4>REX_VALUE[6]</h4>\';\r\n				}\r\n\r\n				if (\'REX_VALUE[id=7 isset=1]\') {\r\n				    $textile = \'REX_VALUE[id=7 output=html]\';\r\n				    echo rex_textile::parse($textile);\r\n				}\r\n\r\n				if (\"REX_LINK[2]\" != \'\') {\r\n					$article = rex_article::get(\"REX_LINK[2]\");\r\n					$article_name = $article->getValue(\'name\');\r\n					if (\"REX_VALUE[8]\" != \'\') {\r\n						$article_name = \'REX_VALUE[8]\';\r\n					}\r\n					echo \'\r\n					<p><br/><a href=\"\'.rex_getUrl(\"REX_LINK[2]\").\'\" class=\"btn btn-theme\">\'.$article_name.\'</a></p>\';\r\n				}\r\n				?>\r\n			</div>\r\n\r\n			<div class=\"col-md-4\">\r\n				<?php\r\n				if (\"REX_VALUE[10]\" != \'\') {\r\n					if (\"REX_VALUE[9]\" != \'\') {\r\n						echo \'\r\n						<i class=\"fa fa-REX_VALUE[9]\"></i>\';\r\n					}\r\n					echo \'\r\n					<h4>REX_VALUE[10]</h4>\';\r\n				}\r\n\r\n				if (\'REX_VALUE[id=11 isset=1]\') {\r\n				    $textile = \'REX_VALUE[id=11 output=html]\';\r\n				    echo rex_textile::parse($textile);\r\n				}\r\n\r\n				if (\"REX_LINK[3]\" != \'\') {\r\n					$article = rex_article::get(\"REX_LINK[3]\");\r\n					// $article_name = $article->getValue(\'name\');\r\n					if (\"REX_VALUE[12]\" != \'\') {\r\n						$article_name = \'REX_VALUE[12]\';\r\n					}\r\n					echo \'\r\n					<p><br/><a href=\"\'.rex_getUrl(\"REX_LINK[3]\").\'\" class=\"btn btn-theme\">\'.$article_name.\'</a></p>\';\r\n				}\r\n				?>\r\n			</div>\r\n		</div>\r\n	</div><! --/container -->\r\n</section><! --/service -->\r\n','<!-- *******************************************************\r\nTEASERKÄSTEN\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>1. Kasten</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"icon1\">Icon</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"icon1\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n\r\n			<br><section class=\"rex-page-section\">\r\n				<div class=\"panel panel-default\">\r\n\r\n					<header class=\"panel-heading collapsed\" data-toggle=\"collapse\" data-target=\"#collapse-REX_SLICE_ID\" aria-expanded=\"false\">\r\n						<div class=\"panel-title\"><i class=\"rex-icon rex-icon-info\"></i> Hinweis zur Eingabe der Icons</div>\r\n					</header>\r\n\r\n					<div id=\"collapse-REX_SLICE_ID\" class=\"panel-collapse collapse\" aria-expanded=\"false\" sty