  (5,'05 . Zitat (auf Bild)','<!-- *******************************************************\r\nQUOTE\r\n******************************************************** -->\r\n<?php\r\n$bg = (\"REX_MEDIA[1]\" != \'\') ? \' style=\"background-image: url(index.php?rex_media_type=full&rex_media_file=REX_MEDIA[1])\"\' : \'\';\r\n?>\r\n\r\n<div class=\"fullpicture parallax clearfix\"<?php echo $bg; ?>>\r\n	<div class=\"layer\"></div>\r\n		<?php\r\n		if ($this->getTemplateId() == 1) {\r\n			echo \'\r\n			<div class=\"container centered\">\';\r\n		} else {\r\n			echo \'\r\n			<div class=\"centered\">\';\r\n		}\r\n		?>\r\n		<div class=\"row\">\r\n			<div class=\"col-lg-8 col-lg-offset-2\">\r\n				<i class=\"fa fa-comment-o\"></i>\r\n				<?php\r\n				if (\'REX_VALUE[id=1 isset=1]\') {\r\n				    $textile = \'REX_VALUE[id=1 output=html]\';\r\n				    echo rex_textile::parse($textile);\r\n				}\r\n				?>\r\n				REX_VALUE[id=\'2\' prefix=\'<h4><br>\' suffix=\'</h4>\']\r\n			</div>\r\n		</div><! --/row -->\r\n	</div><! --/container -->\r\n</div><! --/twrap -->\r\n','<!-- *******************************************************\r\nZITAT AUF BILD\r\n******************************************************** -->\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Titel / Intro</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\">Bild (ca. 2000px breit)</label>\r\n        <div class=\"col-sm-10\">\r\n            REX_MEDIA[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_textile_1\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-textile_full\" id=\"markitup_textile_1\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"person\">Zitierte Person</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"person\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-12-01 10:22:33','2016-03-19 12:38:16','',0),
  (6,'06 . Galerie','<?php\r\n$fe = \'\';\r\n$be = \'\';\r\n\r\nif (\"REX_MEDIALIST[1]\" != \'\') {\r\n	$imagelist = explode(\',\', \"REX_MEDIALIST[1]\");\r\n\r\n	// Rahmen?\r\n	$frame = (\"REX_VALUE[5]\" != \'\') ? \"REX_VALUE[5]\" : \'framed\';\r\n\r\n	// Abstand?\r\n	$padding = (\"REX_VALUE[19]\" != \'\') ? \" REX_VALUE[19]\" : \'\';\r\n\r\n	// Volle Breite?\r\n	$full = (\"REX_VALUE[1]\" == \'full\') ? \' full \' : \' container \';\r\n	// Im Mehrspalten-Template keine volle Breite erlauben\r\n	if ($this->getTemplateId() != 1) { $full = \'\'; }\r\n\r\n	$fe .= \'\r\n	<section id=\"portfoliowrap\" class=\"clearfix\'.$full.$frame.$padding.\'\">\';\r\n\r\n		if (\"REX_VALUE[2]\" != \'\') {\r\n			$fe .= \'\r\n			<REX_VALUE[3]>REX_VALUE[2]</REX_VALUE[3]>\';\r\n			$be .= \'\r\n			<REX_VALUE[3]>REX_VALUE[2]</REX_VALUE[3]>\';\r\n		}\r\n\r\n		$fe .= \'\r\n		<div class=\"portfolio row\">\';\r\n\r\n				foreach ($imagelist as $file) {\r\n					$media = rex_media::get($file);\r\n					if ($media) {\r\n\r\n						$mediatitle = $media->getValue(\'title\');\r\n						$mediadesc = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n						$medialink = $media->getValue(\'med_gallery_link\');\r\n\r\n						$fe .= \'\r\n						<div class=\"col-md-3 portfolio-item\">\r\n							<div class=\"he-wrap pic\">\r\n								<img src=\"index.php?rex_media_type=photos&rex_media_file=\'.$file.\'\" alt=\"\'.$mediadesc.\'\">\r\n								<div class=\"he-view\">\r\n									<div class=\"bg a0\" data-animate=\"fadeIn\">\';\r\n\r\n										if ($mediadesc != \'\' && $this->getTemplateId() == 1) {\r\n											// Nur im Einspalter Bildtexte anzeoigen, da Bilder ansonsten zu klein\r\n											$fe .= \'\r\n											   <h3 class=\"a1\" data-animate=\"fadeInDown\">\'.$mediadesc.\'</h3>\';\r\n										}\r\n\r\n										$fe .= \'\r\n										<a rel=\"prettyPhoto[galREX_SLICE_ID]\" href=\"index.php?rex_media_type=full&rex_media_file=\'.$file.\'\" class=\"dmbutton a2\" data-animate=\"fadeInUp\"><i class=\"fa fa-search\"></i></a>\';\r\n\r\n										if ($medialink != \'\') {\r\n											$fe .= \'\r\n											<a href=\"\'.$medialink.\'\" class=\"dmbutton a2\" data-animate=\"fadeInUp\"><i class=\"fa fa-link\"></i></a>\';\r\n										}\r\n\r\n									  $fe .= \'\r\n								  	</div><!-- he bg -->\r\n								</div><!-- he view -->\r\n							</div><!-- he wrap -->\r\n						</div><!-- end col-12 -->\';\r\n\r\n						$be .= \'\r\n						<img src=\"index.php?rex_media_type=rex_mediapool_detail&rex_media_file=\'.$file.\'\" alt=\"\'.$mediadesc.\'\">\';\r\n\r\n					}\r\n				}\r\n\r\n			$fe .= \'\r\n		</div><!-- portfolio -->\r\n	</section><!--/Portfoliowrap -->\';\r\n\r\n}\r\n\r\n// Unterschiedliche Ausgabe für Frontend und Backend\r\nif (rex::isBackend()) {\r\n	echo $be;\r\n} else {\r\n	echo $fe;\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nGALERIE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Galerie</legend>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h2\'=>\'2. Ebene\',\r\n				\'h3\'=>\'3. Ebene\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[3]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[3]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Bilder</label>\r\n		<div class=\"col-sm-10\">\r\n            REX_MEDIALIST[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n	<?php\r\n	// Im Mehrspalten-Template keine volle Breite erlauben\r\n	if ($this->getTemplateId() == 1) {\r\n	?>\r\n		<div class=\"form-group\">\r\n	        <label class=\"col-sm-2 control-label\" for=\"width\">Breite</label>\r\n	        <div class=\"col-sm-10\">\r\n				<?php\r\n					$options = array(\r\n					\'\'=>\'Contentbreite\',\r\n					\'full\'=>\'volle Beite\'\r\n				);\r\n				?>\r\n				<select name=\"REX_INPUT_VALUE[1]\" id=\"width\" class=\"form-control\">\r\n				<?php foreach ($options as $k=>$v) : ?>\r\n				<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[1]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n				<?php endforeach ?>\r\n				</select>\r\n	        </div>\r\n	    </div>\r\n	<?php } ?>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"width\">Rahmen</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'Rahmen zwischen Bildern\',\r\n				\'frameless\'=>\'ohne Rahmen\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[5]\" id=\"width\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[5]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"distance\">Abstand</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'mt\'=>\'oberhalb\',\r\n				\'mb\'=>\'unterhalb\',\r\n				\'mtb\'=>\'ober- und unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[19]\" id=\"distance\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n\r\n<p>Bilder in der Medienkategorie \"Fotos\" (ID 1) haben ein zusätzliches Linkfeld.</p>\r\n','admin','admin','2015-12-01 10:23:20','2016-03-19 12:39:26','',0),
  (7,'07 . Akkordeon / Tabs (Eingabe)','<?php\r\nif (!isset($counter)) {\r\n	$counter = 0;\r\n}\r\n$counter++;\r\n\r\n// Im Frontend werden die Daten in ein Array geschrieben, aber nicht ausgegeben\r\nif (! rex::isBackend()) {\r\n	$items = array();\r\n	$items = [\'title\' => \'REX_VALUE[2]\', \'text\' => \'REX_VALUE[1]\'];\r\n	rex::getProperty(\'tabs\')->append($items);\r\n// Im Backend wird der Inhalt als Info für den Redakteur angezeigt\r\n} else {\r\n	if (\'REX_VALUE[id=2 isset=1]\') {\r\n	    echo \'<h2>REX_VALUE[2]</h2>\';\r\n	}\r\n	if (\'REX_VALUE[id=1 isset=1]\') {\r\n	    echo \'<p>REX_VALUE[1]</p>\';\r\n	}\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nTABS EINGABE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Tabs Eingabe</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_textile_\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-textile_full\" id=\"markitup_textile_1\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-12-01 10:23:53','2016-03-19 11:52:56','',0),
  (8,'08 . Akkordeon Tabs (Ausgabe)','<?php\r\n\r\n// Im Frontend wird der Inhalt des Arrays ausgelesen und verarbeitet\r\nif (!rex::isBackend()) {\r\n	$items = rex::getProperty(\'tabs\')->getArrayCopy();\r\n\r\n	$tabs_title = \'\';\r\n	$tabs_text = \'\';\r\n	$tabs_out = \'\';\r\n	$counter = 1;\r\n\r\n	$title_style = \'title\';\r\n	$content_style = \'content\';\r\n	if (\"REX_VALUE[1]\" == \'tt\' || \"REX_VALUE[1]\" == \'vt\' || \"REX_VALUE[1]\" == \'bt\') {\r\n		$title_style = \'tab-title\';\r\n		$content_style = \'tab-content\';\r\n	}\r\n	$one_open = (\"REX_VALUE[3]\" == \'one-open\') ? \" one-open\" : \"\";\r\n\r\n	foreach ($items as $item) {\r\n\r\n		if (! isset($item[\'title\'])) { $item[\'title\'] = \'\'; };\r\n		if (! isset($item[\'text\'])) { $item[\'text\'] = \'\'; };\r\n\r\n		$active_title = \'\';\r\n		$active_text = \'\';\r\n\r\n		$tabs_title = $item[\'title\'];\r\n		$tabs_text = $item[\'text\'];\r\n\r\n		if ($tabs_title != \'\' && $tabs_text != \'\') {\r\n			if ($counter == 1) $active_title = \' class=\"active\"\';\r\n\r\n			$textile = rex_textile::parse($tabs_text);\r\n			// Bold-Tags werden \"missbraucht\", um Bootstrap-Labels setzen zu können.\r\n			$textile = str_replace(array(\'<b>\', \'</b>\', \'-&amp;gt;\'), array(\'<span class=\"label label-primary\">\', \'</span>\', \'->\'), $textile);\r\n\r\n			$tabs_out .= \'\r\n			<li\'.$active_title.\'>\r\n				<div class=\"\'.$title_style.\'\">\r\n					<span>\'.$tabs_title.\'</span>\r\n				</div>\r\n				<div class=\"\'.$content_style.\' text\">\'.$textile.\'</div>\r\n			</li>\';\r\n		}\r\n\r\n		$counter++;\r\n\r\n	}\r\n	?>\r\n\r\n	<section class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>tabs\">\r\n			<div class=\"row\">\r\n				<div class=\"col-md-8 col-md-offset-2 col-sm-12\">\r\n\r\n					<?php\r\n					if (\"REX_VALUE[2]\" != \'\') {\r\n						echo \'\r\n						<h3>REX_VALUE[2]</h3>\';\r\n					}\r\n\r\n					if (\"REX_VALUE[1]\" == \'tt\') { // Text-Tabs\r\n						echo \'\r\n						<div class=\"tabbed-content text-tabs\">\';\r\n\r\n					} elseif (\"REX_VALUE[1]\" == \'vt\') { // Vertikale Tabs\r\n						echo \'\r\n						<div class=\"tabbed-content button-tabs vertical\">\';\r\n\r\n					} elseif (\"REX_VALUE[1]\" == \'bt\') { // Button-Tabs\r\n						echo \'\r\n						<div class=\"tabbed-content button-tabs\">\';\r\n					}\r\n\r\n					if (\"REX_VALUE[1]\" == \'ta\') { // Text-Akkordions\r\n\r\n						echo \'\r\n						<ul class=\"accordion accordion-2\'.$one_open.\'\">\'.\r\n							$tabs_out.\'\r\n						</ul>\';\r\n\r\n					} elseif (\"REX_VALUE[1]\" == \'ba\') { // Button-Akkordions\r\n\r\n						echo \'\r\n						<ul class=\"accordion accordion-1\'.$one_open.\'\">\'.\r\n							$tabs_out.\'\r\n						</ul>\';\r\n\r\n					} else {\r\n\r\n						echo \'\r\n						<ul class=\"tabs\">\'.\r\n							$tabs_out.\'\r\n						</ul>\';\r\n\r\n					}\r\n\r\n					if (\"REX_VALUE[1]\" == \'tt\' || \"REX_VALUE[1]\" == \'vt\' || \"REX_VALUE[1]\" == \'bt\') {\r\n						echo \'\r\n						</diV>\';\r\n					}\r\n					?>\r\n\r\n				</div>\r\n			</div>\r\n	</section>\r\n\r\n\r\n	<?php\r\n} else {\r\n	echo \'\r\n	<p>Die Ausgabe erfolgt nur im Frontend.</p>\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nTABS AUSGABE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Tab-Stil</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'ba\'=>\'Button-Akkordeons\',\r\n				\'ta\'=>\'Text-Akkordeons\',\r\n				\'bt\'=>\'Button-Tabs\',\r\n				\'vt\' => \'Vertikale Tabs\',\r\n				\'tt\' => \'Text-Tabs\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[1]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[1]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n	<!--\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">falls Akkordeon</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'one-open\'=>\'Akkordeon schließt anderes\',\r\n				\'multi-open\'=>\'mehrere Akkordions offen\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[3]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[3]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n	-->\r\n</fieldset>\r\n','admin','admin','2015-12-01 10:24:18','2016-03-19 12:38:55','',0),
  (9,'09 . Downloads','<?php\r\n$background = (\"REX_VALUE[16]\" == \'true\') ? \' grey\' : \'\';\r\necho \'\r\n\r\n<div class=\" downloads-wrapper clearfix\'.$background.\'\">\r\n    <div class=\" downloads\';\r\n    if ($this->getTemplateId() == 1) {\r\n        echo \' container\';\r\n    }\r\n    if (\"REX_VALUE[19]\" != \'\') {\r\n        echo \' REX_VALUE[19]\';\r\n    }\r\n    echo \'\">\';\r\n\r\n        if (\"REX_VALUE[17]\" == \'vd\') {\r\n            echo \'\r\n            <div class=\"row\">\r\n            <div class=\"col-md-8 col-md-offset-2\">\';\r\n        }\r\n\r\n        if (\"REX_VALUE[2]\" != \'\') {\r\n            echo \'\r\n            <h3>REX_VALUE[2]</h3>\';\r\n        }\r\n\r\n        if (\"REX_MEDIALIST[1]\" != \'\') {\r\n\r\n            // Dateigröße ermitteln\r\n            if (!function_exists(\'datei_groesse\')) {\r\n                function datei_groesse($URL) {\r\n\r\n                    $groesse = filesize($URL);\r\n                    if($groesse<1000) {\r\n                        return number_format($groesse, 0, \",\", \".\").\" Bytes\";\r\n                    }\r\n                    elseif($groesse<1000000) {\r\n                        return number_format($groesse/1024, 0, \",\", \".\").\" kB\";\r\n                    } else {\r\n                        return number_format($groesse/1048576, 0, \",\", \".\").\" MB\";\r\n                    }\r\n                }\r\n            }\r\n\r\n            // Icon ermitteln\r\n            if (!function_exists(\'parse_icon\')) {\r\n                function parse_icon($ext) {\r\n                    switch (strtolower($ext)) {\r\n                        case \'doc\': return \'<i class=\"fa fa-file-word-o\"></i>\';\r\n                        case \'pdf\': return \'<i class=\"fa fa-file-pdf-o\"></i>\';\r\n                        case \'zip\': return \'<i class=\"fa fa-archive-pdf-o\"></i>\';\r\n                        // nach Belieben ergänzen, z.B. wie hier mit Icons von Font-Awesome\r\n                    default:\r\n                        return \'\';\r\n                    }\r\n                }\r\n            }\r\n\r\n\r\n            $arr = explode(\",\",\"REX_MEDIALIST[1]\");\r\n            foreach ($arr as $value) {\r\n\r\n                $extension = substr(strrchr($value, \'.\'), 1);\r\n                $parsed_icon = parse_icon($extension);\r\n                $media = rex_media::get($value);\r\n                $file_desc = $media->getValue(\'med_description\');\r\n\r\n                echo \'\r\n                <p><a href=\"\'.rex_getUrl(12, $this->clang, array (\'file\' => $value) ).\'\">\'.$parsed_icon;\r\n\r\n                // Beschreibungstext als Linktext, falls vorhanden, ansonsten Dateiname\r\n                if ($file_desc != \"\") {\r\n                    echo $file_desc;\r\n                } else {\r\n                    echo $value;\r\n                }\r\n\r\n                echo \' (\'.datei_groesse(rex_path::media($value)).\')</a></p>\';\r\n\r\n            }\r\n        }\r\n\r\n        if (\"REX_VALUE[18]\"== \'true\'){\r\n            echo \'\r\n            <div class=\"hline\"></div>\';\r\n        }\r\n\r\n        if (\"REX_VALUE[17]\" == \'vd\') {\r\n            echo \'\r\n            </div>\r\n            </div>\';\r\n        }\r\n    echo \'\r\n    </div>\r\n</div>\';\r\n?>\r\n','<!-- *******************************************************\r\nGALERIE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Galerie</legend>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h2\'=>\'2. Ebene\',\r\n				\'h3\'=>\'3. Ebene\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[3]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[3]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Dateien zum Download</label>\r\n		<div class=\"col-sm-10\">\r\n            REX_MEDIALIST[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"padding\">Einrückung</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keine\',\r\n				\'vd\'=>\'ja\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[17]\" id=\"padding\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[17]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"distance\">vertikaler Abstand</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'mt\'=>\'oberhalb\',\r\n				\'mb\'=>\'unterhalb\',\r\n				\'mtb\'=>\'ober- und unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[19]\" id=\"distance\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Trennlinie</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'nein\',\r\n				\'true\'=>\'ja\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[18]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[18]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Hintergrund</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'weiß\',\r\n				\'true\'=>\'grau\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[16]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[16]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n\r\n<p>Bilder in der Medienkategorie \"Fotos\" (ID 1) haben ein zusätzliches Linkfeld.</p>\r\n','admin','admin','2015-12-01 10:24:45','2016-03-19 12:55:41','',0),
  (10,'11 . Code','<!-- *******************************************************\r\nCODE\r\n******************************************************** -->\r\n<div class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>full<?php if (\"REX_VALUE[19]\" != \'\') echo \" REX_VALUE[19]\"; ?>\">\r\n<pre class=\"prettyprint lnaghtml linenums\">\r\nREX_VALUE[1]\r\n</pre>\r\n</div>\r\n','<!-- *******************************************************\r\nCODE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Code</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"code\">Code</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"10\" class=\"form-control rex-code\" id=\"code\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"distance\">Abstand</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'mt\'=>\'oberhalb\',\r\n				\'mb\'=>\'unterhalb\',\r\n				\'mtb\'=>\'ober- und unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"VALUE[19]\" id=\"distance\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-12-01 10:25:30','2016-03-19 12:40:37','',0),
  (11,'12 . Text Seitenspalte','<div class=\"sicetext<?php if (\"REX_VALUE[19]\" != \'\') echo \" REX_VALUE[19]\"; ?>\">\r\n\r\n	<?php\r\n	if (\"REX_VALUE[1]\" != \'\') {\r\n		echo \'\r\n		<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>\';\r\n	}\r\n\r\n	if (\'REX_VALUE[id=3 isset=1]\') {\r\n	    $textile = \'REX_VALUE[id=3 output=html]\';\r\n	    $textile = rex_textile::parse($textile);\r\n	    echo str_replace(array(\'<b>\', \'</b>\'), array(\'<span class=\"label label-primary\">\', \'</span>\'), $textile);\r\n	}\r\n\r\n	if (\"REX_VALUE[18]\"== \'true\'){\r\n		echo \'\r\n		<div class=\"hline\"></div>\';\r\n	}\r\n\r\n	?>\r\n</div>\r\n\r\n<div class=\"spacing\"></div>\r\n','<!-- *******************************************************\r\nTEXT\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h2\'=>\'2. Ebene\',\r\n				\'h3\'=>\'3. Ebene\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[2]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[2]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_1\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n 