  (1,'01 . Standard','<?php\r\ndefine(\'SITE_TYPE\', \'col1\');\r\n?>\r\nREX_TEMPLATE[3]\r\n',1,'admin','admin','2015-12-31 00:13:04','2015-12-31 00:13:04','{\"ctype\":[],\"modules\":{\"1\":{\"0\":\"1\",\"1\":\"2\",\"2\":\"16\",\"3\":\"3\",\"4\":\"4\",\"5\":\"5\",\"6\":\"6\",\"7\":\"7\",\"8\":\"8\",\"9\":\"9\",\"10\":\"18\",\"11\":\"10\",\"12\":\"11\",\"13\":\"17\",\"14\":\"12\",\"all\":0}},\"categories\":{\"all\":\"1\"}}',0),
  (2,'02 . Zweispalter','<?php\r\ndefine(\'SITE_TYPE\', \'col2\');\r\n?>\r\nREX_TEMPLATE[3]\r\n',1,'admin','admin','2016-01-13 22:57:28','2016-01-13 22:57:28','{\"ctype\":{\"1\":\"Hauptspalte\",\"2\":\"Seitenspalte\"},\"modules\":{\"1\":{\"0\":\"1\",\"1\":\"2\",\"2\":\"16\",\"3\":\"3\",\"4\":\"4\",\"5\":\"5\",\"6\":\"6\",\"7\":\"7\",\"8\":\"8\",\"9\":\"9\",\"10\":\"18\",\"11\":\"10\",\"12\":\"17\",\"13\":\"12\",\"all\":0},\"2\":{\"0\":\"11\",\"all\":0},\"3\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (3,'03 . TEMPLATE','REX_TEMPLATE[4]\r\n<body>\r\n\r\n<!-- Fixed navbar -->\r\n<div class=\"navbar navbar-default navbar-fixed-top\" role=\"navigation\">\r\n  <div class=\"container\">\r\n	<div class=\"navbar-header\">\r\n	  <button type=\"button\" class=\"navbar-toggle\" data-toggle=\"collapse\" data-target=\".navbar-collapse\">\r\n		<span class=\"sr-only\">Toggle navigation</span>\r\n		<span class=\"icon-bar\"></span>\r\n		<span class=\"icon-bar\"></span>\r\n		<span class=\"icon-bar\"></span>\r\n	  </button>\r\n	  <a class=\"navbar-brand\" href=\"<?= rex_url::frontend() ?>\"><img src=\"<?= rex_url::base(\'resources/css/images/logo.png\') ?>\" alt=\"REDAXO CMS\"></a>\r\n	</div>\r\n	<div class=\"navbar-collapse collapse navbar-right\">\r\n		<!-- Navigation -->\r\n		REX_TEMPLATE[6]\r\n		<!--/Navigation -->\r\n\r\n	</div><!--/.nav-collapse -->\r\n  </div>\r\n</div>\r\n\r\n<!-- *******************************************************\r\nHeaderpic\r\n******************************************************* -->\r\nREX_TEMPLATE[5]\r\n\r\n<?php\r\n// Breadcrumb\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$secondLevel = $path[2];\r\n\r\nif ($secondLevel != \'\') { // Breadcrumb nur in mindestens der zweiten Ebene anzeigen\r\n	echo \'<div class=\"container breadcrumb-wrapper\">\';\r\n		echo \'Du bist hier: \';\r\n		$startArticle = rex_article::get(rex_article::getSiteStartArticleId());\r\n		$startArticleUrl = $startArticle->getUrl();\r\n		$startArticleName = $startArticle->getName();\r\n		echo \'<a href=\"\'.$startArticleUrl.\'\"><span class=\"home_link\">\'.$startArticleName.\'</a></span>\';\r\n\r\n		$nav = rex_navigation::factory();\r\n		echo $nav->showBreadcrumb(\'\', true);\r\n	echo \'</div>\';\r\n}\r\n\r\n/*\r\nWenn das Template \"01 . Standard\" gewählt ist, geht der Content-Container über die volle Breite.\r\n*/\r\nif (SITE_TYPE == \'col1\') { ?>\r\n	<article class=\"mtb\">\r\n		<?php echo $this->getArticle(\'1\'); ?>\r\n	</article>\r\n<?php }\r\n\r\n/*\r\nWenn das Template \"02 . Zweispalter\" gewählt ist, gibt es eine Hauptspalte und eine Seitenspalte in der Teilung 2/3 - 1/3.\r\n*/\r\nif (SITE_TYPE == \'col2\') { ?>\r\n	<div class=\"container mtb\">\r\n		<div class=\"row\">\r\n\r\n			<div class=\"col-lg-8 col-sm-12\">\r\n				<?php echo $this->getArticle(\'1\'); ?>\r\n			</div>\r\n\r\n			<div class=\"col-lg-3 col-lg-offset-1 sidebar hidden-xs hidden-sm\">\r\n				<!-- Subnavigation -->\r\n				REX_TEMPLATE[8]\r\n				<!-- /Subnavigation -->\r\n\r\n				<!-- ggf. weiterer Content -->\r\n				<?php echo $this->getArticle(\'2\'); ?>\r\n			</div>\r\n\r\n		</div>\r\n	</div>\r\n<?php } ?>\r\n\r\n<!-- *******************************************************\r\n FOOTER\r\n ******************************************************* -->\r\nREX_TEMPLATE[7]\r\n\r\n<!-- REDAXO 5 rocks! -->\r\n\r\n<!-- Javascripts -->\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/jquery.min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/bootstrap.min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/jquery.hoverex.min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/jquery.prettyPhoto.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/jquery.flexslider-min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/prettify.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?= rex_url::base(\'resources/js/redaxo-demo.js\') ?>\"></script>\r\n\r\n</body>\r\n</html>\r\n',0,'admin','admin','2016-03-24 11:15:14','2016-03-24 11:15:14','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (4,'00 . Header','<?php\r\n// error_reporting(0);\r\nerror_reporting(E_ALL);\r\n// Das Error-Reporting sollte nur während der Entwicklung angeschaltet sein. Bei einer Libe-Website sollte es auf 0 gestellt werden.\r\n\r\n// Ist der User nicht eingeloggt?\r\nif (!rex_backend_login::hasSession()) {\r\n	// Ist der aufgerufene Artikel offline?\r\n	if ($this->getValue(\'status\') == 0) {\r\n		// dann zur Fehlerseite weiterleiten\r\n		header (\'HTTP/1.1 301 Moved Permanently\');\r\n		header(\'Location: \'.rex_getUrl(rex_article::getNotFoundArticleId(), rex_clang::getCurrentId()));\r\n		die();\r\n	}\r\n}\r\n\r\n// Benötigt für Eingabe- und Ausgabe-Modul Tabs und Akkordions\r\nrex::setProperty(\'tabs\', new ArrayIterator());\r\n\r\nheader(\'Content-Type: text/html; charset=utf-8\');\r\n?><!DOCTYPE html>\r\n<html lang=\"<?php echo rex_clang::getCurrent()->getCode(); ?>\">\r\n<head>\r\n	<meta charset=\"utf-8\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n	<?php\r\n	// Als Title-Tag den Artikelnamen, außer er wird manuell gesetzt\r\n	if ($this->getValue(\"art_title\") != \"\") {\r\n		$title = htmlspecialchars($this->getValue(\'art_title\'));\r\n	} else {\r\n		$title = htmlspecialchars($this->getValue(\'name\'));\r\n	}\r\n\r\n	echo \'\r\n	<title>\'.$title.\'</title>\';\r\n\r\n	// Keywords and description\r\n	// Wenn der aktuelle Artikel kein Keyword und Description besitzt, werden die aus dem Startartikel genommen\r\n	if ($this->getValue(\"art_keywords\") != \"\") {\r\n		$keywords = $this->getValue(\"art_keywords\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$keywords = $home->getValue(\'art_keywords\');\r\n	}\r\n\r\n	if ($this->getValue(\"art_description\") != \"\") {\r\n		$description = $this->getValue(\"art_description\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$description = $home->getValue(\'art_description\');\r\n	}\r\n\r\n	echo \'\r\n	<meta name=\"keywords\" content=\"\'.htmlspecialchars($keywords).\'\">\';\r\n\r\n	echo \'\r\n	<meta name=\"description\" content=\"\'.htmlspecialchars($description).\'\">\';\r\n	?>\r\n\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/bootstrap.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/redaxo-demo.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/font-awesome.min.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/flexslider.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/forms.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?= rex_url::base(\'resources/css/prettify.css\') ?>\">\r\n\r\n	<!-- HTML5 shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n	<!--[if lt IE 9]>\r\n		<script src=\"https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js\"></script>\r\n		<script src=\"https://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js\"></script>\r\n	<![endif]-->\r\n</head>\r\n',0,'admin','admin','2016-01-13 15:03:34','2016-01-15 11:28:46','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (5,'04 . Headerbild','<?php\r\n// Headerbild rekursiv einbinden.\r\n// Wenn der aktuelle Artikel kein Bild hat, wird das aus den Elternkategorien genommen.\r\n$header = \'\';\r\n$headerpic = $this->getValue(\'art_file\');\r\n$darken = $this->getValue(\'art_darken\');\r\n\r\nif ($headerpic == \'\') {\r\n    $category = rex_article::getCurrent()->getCategory();\r\n    while ($header == \'\') {\r\n        if ($category == null) {\r\n            break;\r\n        }\r\n        $headerpic = $category->getValue(\'art_file\');\r\n        $darken = $category->getValue(\'art_darken\');\r\n        $category = $category->getParent();\r\n    } \r\n}\r\n\r\nif ($headerpic == \'\') {\r\n    $headerpic = rex_article::getSiteStartArticle()->getValue(\'art_file\');\r\n    $darken = rex_article::getSiteStartArticle()->getValue(\'art_file_darken\');\r\n}\r\n\r\n\r\n$header = \'\r\n<div id=\"headerpic\" style=\"background-image: url(media/\'.$headerpic.\');\">\';\r\n\r\n    // Abdunkelung des Headerfotos\r\n    if ($darken == \'\')  $darken = \'0.35\'; // Standardabdunkelung 35%\r\n    $header .= \'\r\n    <div class=\"layer\" style=\"opacity: \'.$darken.\';\"></div>\';\r\n\r\n    $header .= \'\r\n    <div class=\"container centered\">\r\n        <div class=\"row\">\r\n            <h3>\'.$this->getValue(\'name\').\'</h3>\r\n        </div><!-- /row -->\r\n    </div> <!-- /container -->\r\n</div><!-- /headerpic -->\';\r\n\r\n\r\n\r\n// Falls eine Galerie definiert wurde,\r\n// wird diese statt eines Headerbils angezeigt.\r\nif ($this->getValue(\'art_gallery\') != \'\') {\r\n\r\n    $header = \'\r\n    <section class=\"hero-slider slider-fullscreen\">\r\n        <ul class=\"slides\">\';\r\n        \r\n            $imagelist = explode(\',\', $this->getValue(\'art_gallery\'));\r\n            foreach ($imagelist as $file) {\r\n            \r\n                $media = rex_media::get($file);\r\n                if ($media) {\r\n                \r\n                    $mediadesc = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n                    $mediatitle = $media->getValue(\'med_gallery_title\');\r\n                    $mediatext = $media->getValue(\'med_gallery_text\');\r\n                    $medialinktext = $media->getValue(\'med_gallery_link_text\');\r\n                    $medialink = $media->getValue(\'med_gallery_link\');\r\n                    $mediadarken = $media->getValue(\'med_darken\');\r\n            \r\n                    $header .= \'\r\n                    <li class=\"parallax kenburns\">\';\r\n                        \r\n                        // Abdunkelung\r\n                        if ($mediadarken == \'\') $mediadarken = \'0.35\'; // Standardabdunkelung 35%\r\n                        $header .= \'\r\n                        <div class=\"overlay\" style=\"opacity: \'.$mediadarken.\';\"></div>\';\r\n\r\n                        $header .= \'\r\n                        <img alt=\"\'.$mediatitle.\'\" class=\"background-image\" src=\"\'.rex_url::frontendController([\'rex_media_type\' => \'fullscreen\', \'rex_media_file\' => $file]).\'\">\';\r\n\r\n\r\n                        if ($mediatitle != \'\' || $mediatext != \'\') {\r\n                            $header .= \'\r\n                            <div class=\"row slide-content animate\">\r\n                                <div class=\"col-lg-6 col-lg-offset-3 text-center\">\';\r\n\r\n                                    if ($mediatitle != \'\') {\r\n                                        $header .= \'\r\n                                        <h1>\'.$mediatitle.\'</h1>\';\r\n                                    }\r\n                                    if ($mediatitle != \'\') {\r\n                                        $header .= \'\r\n                                        <p class=\"lead\">\'.$mediatext.\'</p>\';\r\n                                    }\r\n                                    if ($medialink != \'\' && $medialinktext != \'\') {\r\n                                        $header .= \'\r\n                                        <a href=\"\'.$medialink.\'\" class=\"btn btn-theme\">\'.$medialinktext.\'</a>\';\r\n                                    }\r\n\r\n                                    $header .= \'\r\n                                </div>\r\n                            </div>\';\r\n                        }\r\n\r\n                    $header .= \'\r\n                    </li>\';\r\n                }\r\n            }\r\n        \r\n        $header .= \'\r\n        </ul>\r\n    </section><!--end of fullscreen slider-->\';\r\n}\r\n\r\necho $header;\r\n?>\r\n',0,'admin','admin','2016-03-19 11:01:03','2016-03-19 11:01:03','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (6,'05 . Navigation','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n$nav_main = \'\';\r\n\r\nforeach (rex_category::getRootCategories() as $lev1) {\r\n\r\n	$hidden_ids = array(1,6);\r\n	// Home und Footer-Hilfsartikel sollen nicht in der Navi auftauchen\r\n	if ($lev1->isOnline(true) && (!in_array($lev1->getId(), $hidden_ids))) {\r\n		\r\n		if ($lev1->getId() == $path1) {\r\n			$nav_main .= \'\r\n			<li class=\"dropdown-toggle active\"><a href=\"\'.$lev1->getUrl().\'\">\'.htmlspecialchars($lev1->getValue(\'name\')).\'</a>\';\r\n		} else {\r\n   			$nav_main .= \'\r\n			<li class=\"dropdown-toggle\"><a href=\"\'.$lev1->getUrl().\'\">\'.htmlspecialchars($lev1->getValue(\'name\')).\'</a>\';\r\n		}\r\n\r\n		// 1st level start\r\n		$lev1Size = sizeof($lev1->getChildren());\r\n\r\n			if ($lev1Size != \"0\") {\r\n\r\n				$nav_main .= \'\r\n				<ul class=\"dropdown-menu\">\';\r\n\r\n					// START 2nd level categories\r\n					foreach ($lev1->getChildren() as $lev2):\r\n						if ($lev2->isOnline(true)) {\r\n\r\n							if ($lev2->getId() == $path2) {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle active\"><a href=\"\'.$lev2->getUrl().\'\">\'.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							} else {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle\"><a href=\"\'.$lev2->getUrl().\'\">\'.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							}\r\n						}\r\n					endforeach;\r\n\r\n				$nav_main .= \'\r\n				</ul>\';\r\n\r\n			}\r\n\r\n		$nav_main .= \'\r\n		</li>\';\r\n	}\r\n}\r\n\r\necho \'\r\n<div class=\"navbar-collapse collapse navbar-right\">\r\n	<ul class=\"nav navbar-nav\">\r\n		\'.$nav_main.\'\r\n	</ul>\r\n</div><!--/.nav-collapse -->\';\r\n?>\r\n',0,'admin','admin','2016-03-19 18:56:20','2016-03-19 18:56:20','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (7,'07 . Footer Hilfstemplate',' <div id=\"footerwrap\">\r\n	<div class=\"container\">\r\n		<div class=\"row\">\r\n			<div class=\"col-md-12\">\r\n\r\n			REX_ARTICLE[id=6]\r\n			<?php\r\n			// Hier wird der Inhalt des Artikels 6 eingebunden. Man könnte es alternativ auch so schreiben:\r\n			// $footer = new rex_article_content(6);\r\n			// echo $footer->getArticle(\'1\');\r\n			?>\r\n\r\n			</div><! --/col -->\r\n		</div><! --/row -->\r\n	</div><! --/container -->\r\n </div><! --/footerwrap -->\r\n',1,'admin','admin','2015-12-31 13:03:03','2015-12-31 13:03:03','{\"ctype\":[],\"modules\":{\"1\":{\"0\":\"13\",\"1\":\"14\",\"2\":\"15\",\"all\":0}},\"categories\":{\"0\":\"6\",\"all\":0}}',0),
  (8,'06 . Subnavigation (Sidebar)','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n$article = rex_article::get($path1);\r\n$articleUrl = $article->getUrl();\r\n$articleName = $article->getName();\r\n\r\n$nav = rex_navigation::factory();\r\n$subnav = $nav->get($path1,2,TRUE,TRUE);\r\n\r\nif ($subnav != \'\') {\r\n\r\n	echo \'\r\n	<h4 id=\"sidenav_title\"><a href=\"\'.$articleUrl.\'\">\'.$articleName.\'</a></h4>\r\n	<div class=\"hline\"></div>\';\r\n\r\n	echo $subnav;\r\n\r\n}\r\n?>\r\n',0,'admin','admin','2015-12-31 07:50:40','2016-01-13 13:58:27','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (9,'08 . Download Hilfstemplate','<?php\r\n$file_name = strtolower(preg_replace(\"/[^a-zA-Z0-9.\\-\\$\\+]/\",\"_\",rex_request(\'file\', \'string\')));\r\n$file_type = \"application/octetstream\";\r\n$file_path = rex_path::media($file_name);\r\n\r\nif ($file_name != \"\" && $fp = @fopen(\"$file_path\", \"r\"))\r\n{\r\n\r\nheader(\"Expires: Mon, 01 Jan 2000 01:01:01 GMT\"); // Date in the past\r\nheader(\"Last-Modified: \" . gmdate(\"D, d M Y H:i:s\") . \" GMT\");// always modified\r\nheader(\"Cache-Control: no-store, no-cache, must-revalidate\");// HTTP/1.1\r\nheader(\"Cache-Control: post-check=0, pre-check=0\", false);\r\nheader(\"Cache-Control: private\");\r\nheader(\"Pragma: no-cache\");\r\nheader(\"Content-Type: $file_type; name=\\\"$file_name\\\"\");\r\nheader(\"Content-Disposition: attachment; filename=\\\"$file_name\\\"\");\r\n\r\nfpassthru ($fp);\r\nfclose($fp);\r\n\r\n}else\r\n{\r\n\r\necho \"<div class=warning>Download fehlgeschlagen</div>\";\r\n\r\n}\r\n?>',1,'admin','admin','2015-12-31 13:18:07','2015-12-31 13:18:07','{\"ctype\":[],\"modules\":{\"1\":{\"all\":0}},\"categories\":{\"0\":\"6\",\"all\":0}}',0);
