INSERT INTO `rex_module` VALUES 
  (1,'01 . Titel / Intro','<?php\r\n// Is there a Headline?\r\nif (\"REX_VALUE[1]\" != \'\') {\r\n\r\n	// If template with one column, use Bootstrap-CSS-class .container to enable indent\r\n	?>\r\n	<div class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>mtb\">\r\n		<div class=\"row\">\r\n			<div class=\"<?php if ($this->getTemplateId() == 1) echo \'col-lg-8 col-lg-offset-2 \'; ?>centered\">\r\n				<h2>REX_VALUE[1]</h2>\r\n				<br>\r\n				<div class=\"hline\"></div>\r\n			</div>\r\n		</div>\r\n	</div><! --/container -->\r\n<?php } ?>\r\n','<!-- *******************************************************\r\nTitel / Intro\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Titel / Intro</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <div class=\"col-sm-12\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-11-30 17:31:33','2016-06-02 12:26:54','',0),
  (2,'02a . Text (Textile und Rex_Markitup)','<?php\r\n$template = ($this->getTemplateId() == 1) ? \' container\' : \'\';\r\n$distance = (\"REX_VALUE[19]\" != \'\') ? \' REX_VALUE[19]\' : \'\';\r\n$background = (\"REX_VALUE[16]\" == \'true\') ? \' grey\' : \'\';\r\n\r\necho \'\r\n<section class=\"module-wrapper clearfix\'.$background.\'\">\r\n<div class=\"full\'.$template.$distance.\'\">\';\r\n\r\n	// Left and right indent?\r\n	if (\"REX_VALUE[17]\" == \'vd\') {\r\n		echo \'\r\n		<div class=\"row\">\r\n		<div class=\"col-md-8 col-md-offset-2\">\';\r\n	}\r\n\r\n	if (\"REX_VALUE[1]\" != \'\') {\r\n		echo \'\r\n		<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>\';\r\n	}\r\n\r\n	if (\'REX_VALUE[id=3 isset=1]\') {\r\n		// Parse text with Textile\r\n	    $textile = \'REX_VALUE[id=3 output=html]\';\r\n	    $textile = rex_textile::parse($textile);\r\n		// Use Bold-Tags to enable Bootstrap-Labels\r\n	    echo str_replace(array(\'<b>\', \'</b>\'), array(\'<span class=\"label label-primary\">\', \'</span>\'), $textile);\r\n	}\r\n\r\n	// Trennlinie?\r\n	if (\"REX_VALUE[18]\"== \'true\'){\r\n		echo \'\r\n		<div class=\"hline\"></div>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[17]\" == \'vd\') {\r\n		echo \'\r\n		</div>\r\n		</div>\';\r\n	}\r\n	?>\r\n</div>\r\n</section>\r\n','<!-- *******************************************************\r\nTEXT\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options 