ion value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[16]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2015-11-30 17:32:04','2016-06-02 12:28:47','',0),
  (3,'03 . Text mit Bild (1 - 3 Spalten)','<?php\r\n$grid_content1 = \'\';\r\n$grid_content2 = \'\';\r\n$grid_content3 = \'\';\r\n\r\n// col1\r\nif (\"REX_MEDIA[1]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[1]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content1 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[1]\">\';\r\n}\r\nif (\'REX_VALUE[id=1 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=1 output=html]\';\r\n    $grid_content1 .= rex_textile::parse($textile);\r\n}\r\n\r\n// col2\r\nif (\"REX_MEDIA[2]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[2]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content2 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[2]\">\';\r\n}\r\nif (\'REX_VALUE[id=2 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=2 output=html]\';\r\n    $grid_content2 .= rex_textile::parse($textile);\r\n}\r\n\r\n// col3\r\nif (\"REX_MEDIA[3]\" != \'\') {\r\n	$media = rex_media::get(\"REX_MEDIA[3]\");\r\n	$mediatitle = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n	$grid_content3 .= \'<img class=\"content\" src=\"index.php?rex_media_type=content&rex_media_file=REX_MEDIA[3]\">\';\r\n}\r\nif (\'REX_VALUE[id=3 isset=1]\') {\r\n    $textile = \'REX_VALUE[id=3 output=html]\';\r\n    $grid_content3 .= rex_textile::parse($textile);\r\n}\r\n?>\r\n<!-- SECTION -->\r\n<div class=\"<?php if ($this->getTemplateId() == 1) echo \'container clearfix \'; ?>pictext<?php if (\"REX_VALUE[19]\" != \'\') echo \" REX_VALUE[19]\"; ?>\">\r\n\r\n	<?php\r\n	if (\"REX_VALUE[10]\" != \'\') {\r\n		echo \'\r\n		<REX_VALUE[11]>REX_VALUE[10]</REX_VALUE[11]>\';\r\n	}\r\n	?>\r\n		<div class=\"row\">\r\n\r\n		<?php\r\n		// 2cols\r\n		if (\"REX_VALUE[9]\" == \'2\')  {\r\n			e